import { Component } from '@angular/core';
import { DegreeType } from 'src/app/Course';
import { StudentserviceService } from 'src/app/service/studentservice.service';
import { University } from 'src/app/University';

@Component({
  selector: 'app-studenthomepage',
  templateUrl: './studenthomepage.component.html',
  styleUrls: ['./studenthomepage.component.css']
})
export class StudenthomepageComponent {

  universities!:University[];

  constructor(private studentservice:StudentserviceService){}

  private filterByDegreeType(degreeType:DegreeType) {
    this.studentservice.filterByDegreeType(degreeType).subscribe(data => {this.universities = data;})


  }

}
