import { Component } from '@angular/core';
import { FormGroup,FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { StudentserviceService } from 'src/app/service/studentservice.service';
import { Student } from 'src/app/Student';

@Component({
  selector: 'app-studentsignup',
  templateUrl: './studentsignup.component.html',
  styleUrls: ['./studentsignup.component.css']
})
export class StudentsignupComponent {


  students !: FormGroup;

  student :Student = new Student();


  constructor(private router : Router , private formbuilder : FormBuilder , private studentservice:StudentserviceService) { 

    this.students = this.formbuilder.group({

    firstname: [,[Validators.required]],
    lasttname:  [,[Validators.required]],
    emailId: [,[Validators.required]],
    password: [,[Validators.required]],
    age: [,[Validators.required]],
    gender:[,[Validators.required]],
    cgpa: [,[Validators.required]],
    degree: [,[Validators.required]],
    aadhaarNo:[,[Validators.required]],
    passportNo: [,[Validators.required]],
    greScore: [,[Validators.required]],
    ieltsScore: [,[Validators.required]],
    specialization: [,[Validators.required]],

    })
  }

  save(){
    this.studentservice.createStudent(this.student).subscribe(data => 
      data=this.student);
  }

  onSubmit(){
    this.student = new Student();

    this.student.firstname = this.form['firstname'].value;
    this.student.lasttname = this.form['lastname'].value;
    this.student.emailId = this.form['emailId'].value;
    this.student.password = this.form['password'].value;
    this.student.age = this.form['age'].value;
    this.student.gender = this.form['gender'].value;
    this.student.cgpa = this.form['cgpa'].value;
    this.student.degree = this.form['degree'].value;
    this.student.aadhaarNo=this.form['aadhaarNo'].value;
    this.student.passportNo = this.form['passportNo'].value;
    this.student.greScore = this.form['greScore'].value;
    this.student.ieltsScore = this.form['ieltsScore'].value;
    this.student.specialization = this.form['specialization'].value;

    this.save();
  }


get form(){
  return this.students.controls;
}


}
<!DOCTYPE html>
<html>
<head>
<meta name="viewport" content="width=device-width, initial-scale=1">

</head>
<body>

<form [formGroup]="students" (submit)="onSubmit()" >
  <div class="container">
    <h1>Sign Up</h1>
    <p>Please fill in this form to create an account.</p>
    <hr>

    <label for="firstname"><b>Firstname</b></label>
    <input type="text" placeholder="Enter firstname" name="firstname" id="firstname" required formControlName="firstname">

    <label for="lastname"><b>Lastname</b></label>
    <input type="text" placeholder="Enter lastname" name="lastname" id="lastname" required  formControlName="lastname">

    <label for="aadhaarno"><b>Aadhaar Number</b></label>
    <input type="number" placeholder="Enter Aadhaar Number" name="aadhaarno" id="aadhaarno" required formControlName="aadhaarno">

    <label for="age"><b>Age</b></label>
    <input type="number" placeholder="Enter Age" name="age" id="age" required formControlName="age">

    <div class="gender">

      <h6 class="genderoption">Gender</h6>

      <div class="form-check form-check-inline">
        <input class="form-check-input" type="radio" name="inlineRadioOptions" id="femaleGender"
          value="option1" checked formControlName="gender"/>
        <label class="form-check-label" for="femaleGender">Female</label>
      </div>

      <div class="form-check form-check-inline">
        <input class="form-check-input" type="radio" name="inlineRadioOptions" id="maleGender"
          value="option2"  formControlName="gender"/>
        <label class="form-check-label" for="maleGender">Male</label>
      </div>

    </div>

    <label for="cgpa"><b>CGPA</b></label>
    <input type="number" placeholder="Current cgpa" name="cgpa" id="cgpa" required>

    <label for="gre"><b>GRE Score</b></label>
    <input type="number" placeholder="Gre Score" name="gre" id="gre" >

    <label for="ielts"><b>IELTS Score</b></label>
    <input type="number" placeholder="Ielts score" name="ielts" id="ielts" ><br/>

    <label for="passportno"><b>Passport Number</b></label>
    <input type="text" placeholder="Enter passport Number" name="passportno" id="passportno" required>

    <label for="specialization"><b>Specialization</b></label>
    <input type="text" placeholder="Enter specialization" name="specialization" id="specialization" required>


    <div class="degreedd">
      <label for="degree">Degree</label >
        <select name="degree" id="degree" required>
          <option value="BTECH">B.Tech</option>
          <option value="BE">BE</option>
          <option value="BSC">B.Sc</option>

        </select>
        <br><br>
    </div>



    <label for="email"><b>Email</b></label>
    <input type="email" placeholder="Enter Email" name="email" id="email" required>

    <label for="pswd"><b>Password</b></label>
    <input type="password" placeholder="Enter Password" name="pswd" id="pswd" required>

    <label for="psw-repeat"><b>Repeat Password</b></label>
    <input type="password" placeholder="Repeat Password" name="psw-repeat" id="psw-repeat" required>
    <hr>


    <button type="submit" class="registerbtn">Register</button>
  </div>

  <div class="container signin">
    <p>Already have an account? <a routerLink="/student">Sign in</a>.</p>
  </div>
</form>

</body>
</html>
