import { Component } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { UniversityserviceService } from 'src/app/service/universityservice.service';
import { University } from 'src/app/University';

@Component({
  selector: 'app-universitysignup',
  templateUrl: './universitysignup.component.html',
  styleUrls: ['./universitysignup.component.css']
})
export class UniversitysignupComponent {





  universities !:FormGroup;

  university :University = new University();

  constructor(private router : Router , private formbuilder : FormBuilder ,private universityservice:UniversityserviceService){

    this.universities = this.formbuilder.group({

      instituteCode:[,Validators.required],   
      universityName:[,Validators.required], 
      country:[,Validators.required],       
      state:[,Validators.required],      
      minimumCgpa:[],    
      minimumIeltsScore:[],
      minimumGreScore:[], 
      seatsAvailable:[],  
      emailId:[,Validators.required,Validators.email],      
      password:[,Validators.required],      
    })

  }

  OnlyNumbersAllowed(event: { which: any; keyCode: any; }):boolean {
    const charCode=(event.which)?event.which:event.keyCode;
    if(charCode>31&&(charCode<48||charCode>57)){
      console.log('charCode is restricted is'+charCode);
      return false;
    }
     return true;
  }

  OnlyLettersAllowed(event: { which: any; keyCode: any; }):boolean {
    const charCode=(event.which)?event.which:event.keyCode;
    if((charCode==20)&&(charCode<97||charCode>122)&&(charCode<65||charCode>90)){
      console.log('charCode is restricted is'+charCode);
      return false;
    }
     return true;
  }


  saveUniversity(){
    this.universityservice.addUniversity(this.university).subscribe(data => 
      data=this.university);

      alert("University Sign Up Successful");

      this.goToUniversityHomepage();
  }



  onSubmit(){
    this.university = new University();

    this.university.instituteCode 	= this.form['instituteCode'].value;
    this.university.universityName	= this.form['universityName'].value;
    this.university.country		= this.form['country'].value;
    this.university.state		= this.form['state'].value;
    this.university.minimumCgpa	= this.form['minimumCgpa'].value;
    this.university.minimumIeltsScore	= this.form['minimumIeltsScore'].value;
    this.university.minimumGreScore	= this.form['minimumGreScore'].value;

    this.university.emailId		= this.form['emailId'].value;
    this.university.password 		= this.form['password'].value;


    this.saveUniversity();
  }

get form(){
  return this.universities.controls;
}

goToUniversityHomepage(){
  this.router.navigate(['/university']);
}


}
<!DOCTYPE html>
<html>
<head>
<meta name="viewport" content="width=device-width, initial-scale=1">

</head>

<body>

  <form [formGroup]="universities" (submit)="onSubmit()">
    <div class="container">
      <h1>Sign Up</h1>
      <p>Please fill in this form to create an account.</p>
      <hr>

      <label for="instituteCode"><b>Institute Code</b></label>
      <input type="text" (keypress)="OnlyNumbersAllowed($event)" pattern="([0-9]{4})" placeholder="Enter instituteCode" name="instituteCode" id="instituteCode" required
        formControlName="instituteCode">

        <div  *ngIf="universities.controls['instituteCode'].invalid && (universities.controls['instituteCode'].dirty || universities.controls['instituteCode'].touched)" class="alert">
          <div *ngIf="universities.controls['instituteCode'].errors" style="color: red;">
          *Please Enter Your Four Digit Institute Code 
        </div>
      </div>

      <label for="universityName"><b>University Name</b></label>
      <input type="text" (keypress)="OnlyLettersAllowed($event)" pattern="([a-zA-z_ ]{3,50})" placeholder="Enter universityName" name="universityName" id="universityName" required 
      formControlName="universityName">

      <div  *ngIf="universities.controls['universityName'].invalid && (universities.controls['universityName'].dirty || universities.controls['universityName'].touched)" class="alert">
        <div *ngIf="universities.controls['universityName'].errors" style="color: red;">
        *Please Enter Your University Name 
      </div>
    </div>

      <label for="country"><b>Country</b></label>
      <input type="text" (keypress)="OnlyLettersAllowed($event)" pattern="([a-zA-z_ ]{1,50})" placeholder="Enter country" name="country" id="country" required 
      formControlName="country">

      <div  *ngIf="universities.controls['country'].invalid && (universities.controls['country'].dirty || universities.controls['country'].touched)" class="alert">
        <div *ngIf="universities.controls['country'].errors" style="color: red;">
        *Please Enter Your Country
      </div>
    </div>


      <label for="state"><b>State</b></label>
      <input type="text" (keypress)="OnlyLettersAllowed($event)" pattern="([a-zA-z_ ]{1,50})"  placeholder="Enter state" name="state" id="state" required 
      formControlName="state">

      <div  *ngIf="universities.controls['state'].invalid && (universities.controls['state'].dirty || universities.controls['state'].touched)" class="alert">
        <div *ngIf="universities.controls['state'].errors" style="color: red;">
        *Please Enter Your State
      </div>
    </div>

      <label for="minimumCgpa"><b>Minimum CGPA</b></label>
      <input type="number" (keypress)="OnlyNumbersAllowed($event)" placeholder="Enter Minimum CGPA" name="minimumCgpa" id="minimumCgpa" required
        formControlName="minimumCgpa">

        <div  *ngIf="universities.controls['minimumCgpa'].invalid && (universities.controls['minimumCgpa'].dirty || universities.controls['minimumCgpa'].touched)" class="alert">
          <div *ngIf="universities.controls['minimumCgpa'].errors" style="color: red;">
          *Please Enter Your Required Cgpa between 0-10
        </div>
      </div>

      <label for="minimumIeltsScore"><b>Minimum IELTS Score</b></label>
      <input type="number" (keypress)="OnlyNumbersAllowed($event)" placeholder="Enter Minimum IELTS Score" name="minimumCgpa" id="minimumIeltsScore" required
        formControlName="minimumIeltsScore">


        <div  *ngIf="universities.controls['minimumIeltsScore'].invalid && (universities.controls['minimumIeltsScore'].dirty || universities.controls['minimumIeltsScore'].touched)" class="alert">
          <div *ngIf="universities.controls['minimumIeltsScore'].errors" style="color: red;">
          *Please Enter Your Required Ielts Score between 0-9
        </div>
      </div>

      <label for="minimumGreScore"><b>Minimum GRE Score</b></label>
      <input type="number" (keypress)="OnlyNumbersAllowed($event)" placeholder="Enter Minimum GREScore" name="minimumGreScore" id="minimumGreScore" required
        formControlName="minimumGreScore">


        <div  *ngIf="universities.controls['minimumGreScore'].invalid && (universities.controls['minimumGreScore'].dirty || universities.controls['minimumGreScore'].touched)" class="alert">
          <div *ngIf="universities.controls['minimumGreScore'].errors" style="color: red;">
          *Please Enter Your Required Gre Score between 0-360
        </div>
      </div>


     <label for="emailId"><b>Email Id </b></label>
      <input type="email" pattern="(^[a-zA-Z0-9+_.-]+@[a-zA-Z0-9.-]+$)" placeholder="Enter Email Id" name="emailId" id="emailId" required 
       formControlName="emailId">

       <div  *ngIf="universities.controls['emailId'].invalid && (universities.controls['emailId'].dirty || universities.controls['emailId'].touched)" class="alert">
        <div *ngIf="universities.controls['emailId'].errors" style="color: red;">
        *Please Enter Your Email Id
      </div>
    </div>

     <label for="password"><b>Password </b></label>
      <input type="password" pattern="([A-Za-z0-9]{3,50})" placeholder="Enter password" name="password" id="password" required 
       formControlName="password">

       <div  *ngIf="universities.controls['password'].invalid && (universities.controls['password'].dirty || universities.controls['password'].touched)" class="alert">
        <div *ngIf="universities.controls['password'].errors" style="color: red;">
        *Please Enter Your Password
      </div>
    </div>



      <button type="submit" class="registerbtn"  [disabled]="universities.invalid">Register</button>
    </div>

    <div class="container signin">
      <p>Already have an account? <a routerLink="/university">Sign in</a>.</p>
    </div>
  </form>

</body>

</html>
