import { Component } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { Course } from 'src/app/Course';
import { UniversityserviceService } from 'src/app/service/universityservice.service';

@Component({
  selector: 'app-coursesignup',
  templateUrl: './coursesignup.component.html',
  styleUrls: ['./coursesignup.component.css']
})
export class CoursesignupComponent {

  instituteCode!:number;

  courseForm!: FormGroup;
  course: Course = new Course();
  submitted = false;
  // instituteCode!:number;
  instituteCodeForm!: FormGroup;

  constructor(
   private fb: FormBuilder, 
    private universityservice:UniversityserviceService, 
    private router: Router,
    private route: ActivatedRoute) {

      this.instituteCodeForm = this.fb.group({
        instituteCode : []


      });

    this.courseForm = this.fb.group({
      courseCode: [],
      courseName: [],
      courseDuration: [],
      degreeType: []

      });
   }

  ngOnInit(): void {
    this.course = new Course();

   this.instituteCode = this.route.snapshot.params['instituteCode'];



  }


  onSubmit1(){
    this.course = new Course();
    this.course.courseCode = this.form['courseCode'].value;
    this.course.courseName = this.form['courseName'].value
    this.course.courseDuration = this.form['courseDuration'].value

    this.course.degreeType = this.form['degreeType'].value


    this.instituteCode = this.form1['instituteCode'].value;

    console.log(this.instituteCode);
   console.log(this.course);
    this.submitted = true;
    this.saveCourse();
  }

  get form(){
    return this.courseForm.controls;
  } 
  get form1(){
    return this.instituteCodeForm.controls;
    console.log(this.instituteCodeForm);
  } 


  saveCourse(){
    this.universityservice.addCourse(this.instituteCode,this.course)
      .subscribe(data => {data=this.course})
      alert("Student Sign Up Successful")
      this.goToUniversityHomepage();

  }

  goToUniversityHomepage(){
    this.router.navigate(['/universityhomepage']);
  }


}
<body>

	<div class="form_wrapper">

		<div class="form_container">
			<div class="title_container">
				<h2>Create Course</h2>
			</div>

			<div class="row clearfix">
				<div class="">
					<form id='register-form' autocomplete="off" [formGroup]="courseForm" (ngSubmit)="onSubmit1()">
						<div class="row clearfix">

							<div class="col_half">

								<div class="input_field">
									<label>Enter Course Code</label>
									<input type="text" name="courseCode" placeholder="ex. 101"
										formControlName="courseCode">
								</div>
							</div>

							<div class="col_half">
								<div class="input_field">
									<label>Enter Course Name</label>
									<input type="text" name="courseName" placeholder="ex. Data Science"
										formControlName="courseName" >
								</div>
							</div>

						</div>

						<div class="enum1">
						<div class="duration">
							<label for="courseDuration">Choose Course Duration</label>
							<select id="courseDuration" name="courseDuration" formControlName="courseDuration">
								<option value="ONE">One Year</option>
								<option value="TWO">Two Years</option>
								<option value="THREE">Three Years</option>
							</select>

						</div>

					</div>

					<div class="enum2">
						<div class="degree">
							<label for="degreeType">Choose Degree Type</label>
							<select id="degreeType" name="degreeType" formControlName="degreeType">
								<option value="PGD">Pg.Diploma</option>
								<option value="MASTERS">Masters</option>
								<option value="PHD">Ph.D</option>
							</select>

						</div>
					</div>	

						<!-- For Submission & attaching with university -->

						<form [formGroup]="instituteCodeForm">
							<div class="input_field">
