import { Component } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { Course } from 'src/app/Course';
import { UniversityserviceService } from 'src/app/service/universityservice.service';

@Component({
  selector: 'app-coursesignup',
  templateUrl: './coursesignup.component.html',
  styleUrls: ['./coursesignup.component.css']
})
export class CoursesignupComponent {

  // title: string = 'Add ';
  // id!:number;

  courseForm!: FormGroup;
  course: Course = new Course();
  submitted = false;
  instituteCode!:number;
  instituteCodeForm!: FormGroup;

  constructor(
   private fb: FormBuilder, 
    private universityservice:UniversityserviceService, 
    private router: Router,
    private route: ActivatedRoute) {

      this.instituteCodeForm = this.fb.group({
          instituteCode : []


      });

    this.courseForm = this.fb.group({
      courseCode: [],
      courseName: [],
      courseDuration: [],
      degreeType: []

      });
   }

  ngOnInit(): void {
    this.course = new Course();

   this.instituteCode = this.route.snapshot.params['instituteCode'];



  }


  onSubmit1(){
    this.course.courseCode = this.form['courseCode'].value;
    this.course.courseName = this.form['theatreAddressNo'].value
    this.course.courseDuration = this.form['courseName'].value
    this.course = new Course();
    this.course.degreeType = this.form['degreeType'].value


    this.instituteCode = this.form1['instituteCode'].value;

    console.log(this.instituteCode);
   console.log(this.course);
    this.submitted = true;
    this.saveCourse();
  }

  get form(){
    return this.courseForm.controls;
  } 
  get form1(){
    return this.instituteCodeForm.controls;
    console.log(this.instituteCodeForm);
  } 


  saveCourse(){
    this.universityservice.addCourse(this.course,this.instituteCode)
      .subscribe(data => { data=this.course})

  }




}
<body>

<div class="form_wrapper">
	<div class="form_container">
		<div class="title_container">
			<h2>Add Course</h2>
		</div>
		<div class="row clearfix">
			<div class="">
				<form id='register-form' autocomplete="off" [formGroup]="courseForm" (ngSubmit)="onSubmit1()">
					<div class="row clearfix">
						<div class="col_half">
							<div class="input_field">
								<span>
									<i aria-hidden="true" class="fa fa-user"></i>
								</span>
								<input type="text" name="courseCode" placeholder="Course Code"
									formControlName="courseCode">
							</div>
						</div>
						<div class="col_half">
							<div class="input_field">
								<span>
									<i aria-hidden="true" class="fa fa-user"></i>
								</span>
								<input type="text" name="courseName" placeholder="Course Name"
									formControlName="courseName">
							</div>
						</div>
					</div>

					<div class="input_field">
						<span>
							<i aria-hidden="true" class="fa fa-envelope"></i>
						</span>
						<input type="text" name="courseDuration" placeholder="Course Duration"
							formControlName="courseDuration">
					</div>

					<div class="input_field">
						<span>
							<i aria-hidden="true" class="fa fa-envelope"></i>
						</span>
						<input type="text" name="degreeType" placeholder="Degree Type" formControlName="degreeType">
					</div>

					<form [formGroup]="instituteCodeForm">
						<div class="input_field">
							<span>
								<i aria-hidden="true" class="fa fa-envelope"></i>
							</span>
							<input type="text" name="instituteCode" placeholder="instituteCode"
								formControlName="instituteCode">
						</div>
					</form>



					<input id='submit-data' class="button" type="submit" value="Register" />
				</form>
			</div>
		</div>
	</div>
</div>
</body>


