import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { CookieService } from 'ngx-cookie-service';
import { Course } from 'src/app/Course';
import { StudentserviceService } from 'src/app/service/studentservice.service';
import { UniversityserviceService } from 'src/app/service/universityservice.service';
import { University } from 'src/app/University';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-universitycourselist2',
  templateUrl: './universitycourselist2.component.html',
  styleUrls: ['./universitycourselist2.component.css']
})
export class Universitycourselist2Component {

  searchName!:any;

  instituteCode!: number;
  courses!: Course[];
  university!: University;
  aadhaarNo!: any;
  course!: Course;

  constructor(private studentService: StudentserviceService,
    private router: Router, private cookie: CookieService, private route: ActivatedRoute, private universityService: UniversityserviceService) { }

  ngOnInit(): void {
    this.course = new Course();
    this.aadhaarNo = this.cookie.get('aadhaarNo')
    this.instituteCode = this.route.snapshot.params['instituteCode'];
    this.studentService.getUniversityCourseList(this.instituteCode)
      .subscribe(data => {
        console.log(data)
        this.courses = data;
        console.log(this.courses)

      }
      );
    this.universityService.getUniversityById(this.instituteCode).subscribe(data => {
      console.log(data)
      this.university = data;
      console.log(this.university)
    });


  }


  //logout
  logOut() {
    this.cookie.delete('instituteCode');
    this.cookie.delete('emailId');

  }

  apply(courseCode: Number) {
    this.studentService.applyUniversity(this.instituteCode, courseCode, this.aadhaarNo, this.course)
      .subscribe(data => { },
        (error) => {
          if (error?.status == 409) {
            // alert("You cannoty able to  applly");
            Swal.fire({
              icon: 'error',
              title: 'Oops...',
              text: 'You are not eligible to apply!',
            })
          }
          else {
            Swal.fire({
              icon: 'success',
              title: 'Applied Successfully',
              text: 'You have successfully applied to this course',

            })
            //  alert("Successfully Applied to " + this.university.universityName);
            this.router.navigate(['/studenthomepage']);


          }

        }


      );



    // this.goToStudentHomepage;
  }


  // goToStudentHomepage(){
  //   this.router.navigate(['/student']);
  // }

}

<html>
<nav class="navigation">
  <div class="container-fluid">
    <ul class="nav">
      <div class="form-outline"  style="margin-top: -10px;margin-bottom: -10px;margin-right: 10px;">
        <input type="search" id="form1" class="form-control" placeholder="search" [(ngModel)]="searchName" name="search"/>
        
      </div>
      <li onclick="history.back()">back</li>
     
    </ul>
  </div>
</nav>
  <body>
    <div *ngIf="courses==null">
      <h1>NO COURSES AVAILABLE</h1>
  </div>

  <div *ngIf="courses!=null">

  
<section id="gallery">
  <h1 style="text-align: center;margin-bottom: 20px;">COURSE LIST</h1>
  <div class="row">
   
  </div>
  <div class="container">
    <div class="row" >
    <div class="col-lg-4 mb-4" *ngFor="let course of courses | coursesearch:searchName ">
    <div class="card" >
      <!-- <img src="https://images.unsplash.com/photo-1477862096227-3a1bb3b08330?ixlib=rb-1.2.1&auto=format&fit=crop&w=700&q=60" alt="" class="card-img-top"> -->
      <div class="card-body">
        <h5 class="card-title">{{course.courseName}}</h5>
        <p class="card-text">{{course.courseCode}}</p>
        <p class="card-text">{{course.degreeType}}  </p>
        <p class="card-text">{{course.courseDuration}}-Year/Years</p>
        <button  (click) = "apply(course.courseCode)"class = "btn btn-primary"> Apply</button>
        
      </div>
     </div>
    </div>
 
   
  </div>
</div>
</section> 
   
  </div>
  </body>
</html>
